import React, { Component, Fragment } from 'react';
import { Redirect } from 'react-router-dom'
// import Axios from 'axios';
import './Profil.css';

class Profil extends Component {

    state = {
        user: this.props.match.params.name,
        goToHome: false,
        age: 0,
        mail: '',
        department: ''
    }

    confirmProfil = () => {
        this.setState({ goToHome: true })
    }

    handleAge = (e) => {
        const age = e.target.value
        this.setState({ age })
    }

    handleMail = (e) => {
        const mail = e.target.value
        this.setState({ mail })
    }

    handleDepartment = (e) => {
        const department = e.target.value
        this.setState({ department })
    }

    // https://www.youtube.com/watch?v=XeiOnkEI7XI

    render() {


        if (this.state.goToHome) {
            return <Redirect push to={`/home/${this.state.user}`} />
        }


    return ( 
        <Fragment>
            <div className="conteneur">
                <h1 className='title'>Profil de {this.state.user}</h1>
                <h3>Age</h3>
                <input type="text" placeholder="33" onChange={this.handleAge} />
                <h3>Adresse mail</h3>
                <input type="text" placeholder="nom@gmail.com" onChange={this.handleMail} />
                <h3>Service</h3>
                <input type="text" placeholder="RH" onChange={this.handleDepartment} />
                <h3>Ajouter une photo de profil</h3>

                <button onClick={this.confirmProfil}>Valider son profil</button>
            </div>
        </Fragment>
    )
  }
}

export default Profil;
