const initState = {
    user: {
        id: 0,
        username: 'toto',
        password: '',
        age: 0,
        mail: '',
        department: ''
    }
}

const rootReducer = (state = initState, action) => {

    if (action.type === 'USER') {

        let user = state.user
        const type = action.payload
        user.id = type.id
        user.username = type.username
        user.password = type.password

        return {
            ...state,
            user: user
        }
    }
    return state;
}

export default rootReducer;





