import React, { Component } from 'react'
import './PostHome.css';
import Axios from 'axios'

class PostComment extends Component {

    state = {
        inputCommentValue: '',
        comment: []
    }

    addCommentChange = (e) => {
        this.setState({ inputCommentValue: e.target.value })
    }

    addCommentPress = (e, idposts) => {
        if (e.key === 'Enter') {

            let getTokenStringify = localStorage.getItem("token");
            let getToken = JSON.parse(getTokenStringify);

            Axios.post(`${process.env.REACT_APP_API_URL}/addComment/${getToken[0]}`, {
                postId: idposts+'',
                comment: e.target.value+''
            }, { headers: {"Authorization" : `Bearer ${getToken[1]}`} }).then((response) => {

                // this.getComment()
                // let comment = this.state.comment
                // comment.push([e.target.value)


                this.setState({ inputCommentValue: '' })
            })

        } 
    }

    render() {

        const props = this.props
        console.log(props)

        this.setState({ comment: props.comments })
        let lastComment = []

        this.state.comments.forEach((comment, commentIndex) => {

            lastComment.push(
                <p key={'theComment'+commentIndex} className='theComment'>
                    {comment.comment}
                </p>
            )

        })


        return (

            <div>
                {comments}
                <input
                    type="text"
                    value={this.state.inputCommentValue}
                    className="addCommentInput"
                    placeholder="ajouter un commentaire..."
                    onChange={(e) => this.addCommentChange(e)}
                    onKeyPress={(e) => this.addCommentPress(e, props.idposts)}
                />
            </div>
        )
    }
}


