import React, { Component, Fragment } from 'react';
import { Redirect } from 'react-router-dom'
import Axios from 'axios';
import './Profil.css';

class Profil extends Component {

    state = {
        user: this.props.match.params.id,
        goToHome: false,
        age: 0,
        mail: '',
        department: '',
        selectedFile: null
    }

    componentDidMount () {

        Axios.get(`http://localhost:3001/user/${this.state.user}`).then((response) => {
            this.setState({ user: response.data[0].username })
        })

    }

    confirmProfil = () => {

        Axios.post(`http://localhost:3001/u:${this.state.age}/:${this.state.mail}/:${this.state.department}`).then((response) => {
            console.log('profil mis Ã  jour')
        })

        this.setState({ goToHome: true })
    }

    handleAge = (e) => {
        const age = e.target.value
        this.setState({ age })
    }

    handleMail = (e) => {
        const mail = e.target.value
        this.setState({ mail })
    }

    handleDepartment = (e) => {
        const department = e.target.value
        this.setState({ department })
    }

    fileSelectedHandler = (e) => {
        console.log(e.target.files[0])
        this.setState({ selectedFile: e.target.files[0] })
    }

    fileUploadHandler = () => {
        const fd = new FormData();
        fd.append('image', this.state.selectedFile, this.state.selectedFile.name)
        Axios.post('http://localhost:3001/image')
    }


    render() {


        if (this.state.goToHome) {
            return <Redirect push to={`/home/${this.state.user}`} />
        }


    return ( 
        <Fragment>
            <div className="conteneur">
                <h1 className='title'>Profil de {this.state.user}</h1>
                <h3>Age</h3>
                <input type="text" placeholder="33" onChange={this.handleAge} />
                <h3>Adresse mail</h3>
                <input type="text" placeholder="nom@gmail.com" onChange={this.handleMail} />
                <h3>Service</h3>
                <input type="text" placeholder="RH" onChange={this.handleDepartment} />
                <h3>Photo de profil</h3>
                <input type="file" onChange={this.fileSelectedHandler}/>
                <button onClick={this.fileUploadHandler}>Upload</button>
                <button onClick={this.confirmProfil}>Valider son profil</button>
            </div>
        </Fragment>
    )
  }
}

export default Profil;
