import React, { Component, Fragment } from 'react'
import { Redirect } from 'react-router-dom'
import './ProfilHome.css';
import { connect } from 'react-redux'

class ProfilHome extends Component {

  state = {
    userProfil: '',
    goToProfil: false,
  }

  componentDidMount () {

    const { user } = this.props;
    this.setState({ userProfil: user })

  }

  handleGoToProfil = () => {
    this.setState({ goToProfil: true })
  }



  render() {


    if (this.state.goToProfil) {
        return <Redirect push to={`/profil`} />
    }

    return (
      <Fragment>
        <div>
          <h2>Profil utilisateur</h2>
          <h2>{this.state.userProfil.username}</h2>
          <h2>age : {this.state.userProfil.age} ans</h2>
          <h2>Adresse mail :</h2>
          <h2>{this.state.userProfil.mail} ans</h2>  
          <button onClick={this.handleGoToProfil}>modifier son profil</button>
        </div>
      </Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(mapStateToProps)(ProfilHome);
