import React, { Component, Fragment } from 'react'
import { Redirect } from 'react-router-dom'
import ProfilHome from '../../components/ProfilHome/ProfilHome'
import './Home.css';
import { connect } from 'react-redux'

class Home extends Component {

  state = {
    username: '',
    goToProfil: false,
  }

  componentDidMount () {

    const { user } = this.props;
    this.setState({ username: user.username })

  }

  handleGoToProfil = () => {
    this.setState({ goToProfil: true })
  }


  render() {
    const name = 'yellow'

    if (this.state.goToProfil) {
        return <Redirect push to={`/profil`} />
    }

    return (
      <Fragment>
        <div className="conteneur">
          <div className="post">
            <h1>Bienvenue {this.state.username}</h1>
            <p>Groupomania, un r√©seau social</p>
          </div>
          <div className="profil">
            <ProfilHome 
              color='yellow'

            ></ProfilHome>
          </div>

        </div>
      </Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(mapStateToProps)(Home);
