import React, { Component, Fragment } from 'react';
import { Redirect } from 'react-router-dom'
import Axios from 'axios';
import '../App.css';

class Connexion extends Component {

    state = {
        usernameReg: '',
        passwordReg: '',
        username: '',
        password: '',
        loginStatus: ''
    }

        handleUsernameReg = e => {
            const usernameReg = e.target.value
            this.setState({ usernameReg })
        }

        handlePasswordReg = e => {
            const passwordReg = e.target.value
            this.setState({ passwordReg })
        }

        handleUsername = e => {
            const username = e.target.value
            this.setState({ username })
        }

        handlePassword = e => {
            const password = e.target.value
            this.setState({ password })
        }

        register = () => {

            Axios.post('http://localhost:3001/register', {
                username: this.state.usernameReg, 
                password: this.state.passwordReg
            })     
        }

        login = () => {
            Axios.post('http://localhost:3001/login', {
                username: this.state.username, 
                password: this.state.password
            }).then((response) => {
                if (response.data.message) {
                    const loginStatus = response.data.message
                    this.setState({ loginStatus })
                } else {
                    const loginStatus = response.data[0].username
                    this.setState({ loginStatus })
                }
            })
        }

    render() {

        if (this.state.goToHome) {
            return <Redirect push to={`/user/${this.state.username}`} />
        }

    return (
        <Fragment>
            <div className="App">
                <div className="registration">
                <h1>Registration</h1>
                <label>Username</label>
                <input type="text" onChange={this.handleUsernameReg} />
                <label>Password</label>
                <input type="password" onChange={this.handlePasswordReg} />
                <button onClick={this.register}> Register </button>
                </div>
                <div className="login">
                <h1>Login</h1>
                <input type="text" placeholder="Username..." onChange={this.handleUsername} />
                <input type="password" placeholder="Password..." onChange={this.handlePassword} />
                <button onClick={this.login}> Register </button>
                </div>

                <h1>{ this.state.loginStatus }</h1>
            </div>
        </Fragment>
    )
    }
}

export default Connexion;


