import React, { Component, Fragment } from 'react'
import { Redirect } from 'react-router-dom'
// import './ProfilHome.css';
import { connect } from 'react-redux'

class ProfilHome extends Component {

  state = {
    username: '',
    goToProfil: false,
  }

  componentDidMount () {

    const { user } = this.props;
    this.setState({ username: user.username })

  }

  handleGoToProfil = () => {
    this.setState({ goToProfil: true })
  }



  render() {

    const color = {
      color: this.props.color,
      width: this.props.color,
      height: this.props.color
    }

    if (this.state.goToProfil) {
        return <Redirect push to={`/profil`} />
    }

    return (
      <Fragment>
        <div style={color}>
        <h1>Profil utilisateur {this.props.color} </h1>
        <h1>{this.state.username}</h1>
          <p>Groupomania, un r√©seau social</p>

          <button onClick={this.handleGoToProfil}>modifier son profil</button>
        </div>
      </Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  }
}

export default connect(mapStateToProps)(ProfilHome);
