import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import ProfilHome from '../../components/ProfilHome/ProfilHome'
import PostHome from '../../components/PostHome/PostHome'
import './Home.css';
import Axios from 'axios'

class Home extends Component {

  state = {
    userprofil: '',
    goToProfil: false,
  }


  // `${process.env.REACT_APP_API_URL}/profile/${auth.userId}`,
  //                       "GET",
  //                       null,
  //                       {
  //                           Authorization: "Bearer " + auth.token,
  //                       }
  //                   );
  componentDidMount () {

    let getTokenStringify = localStorage.getItem("token");
    let getToken = JSON.parse(getTokenStringify);

    console.log(getToken)
    //{ headers: {"Authorization" : `Bearer ${getToken[1]}`} }

    Axios.get('http://localhost:3001/getProfil/', {id: getToken[0]}).then((response) => {
            console.log(response.data)
            //  this.setState({ userprofil: user })

        })     


  }

  handleGoToProfil = () => {
    this.setState({ goToProfil: true })
  }

  addComment = () => {
    console.log('add comment')



  }


  render() {

    if (this.state.goToProfil) {
        return <Redirect push to={`/profil`} />
    }

    return (
        <div id="conteneur">
          <section className="profil">
            <ProfilHome></ProfilHome>
          </section>
          <section className="post">
              <div id="postView">
                  <PostHome></PostHome>
              </div>
              <div id="homeBottomBar">
                  <button onClick={this.addComment}>Ajouter un commentaire</button>
                  <button>Ajouter un post</button>
              </div>
          </section>
        </div>
    )
  }
}


export default Home;
