import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import Axios from 'axios';
import './Connexion.css';
import { connect } from 'react-redux'
import logo from '../../img'
class Connexion extends Component {

    state = {
        usernameReg: '',
        passwordReg: '',
        mailReg: '',
        id: 0,
        mail: '',
        password: '',
        loginStatus: '',
        goToProfil: false,
        goToHome: false
    }

    handleUsernameReg = e => {
        const usernameReg = e.target.value
        this.setState({ usernameReg })
    }

    handlePasswordReg = e => {
        const passwordReg = e.target.value
        this.setState({ passwordReg })
    }

    handleUserMailReg = e => {
        const mailReg = e.target.value
        this.setState({ mailReg })
    }

    handleMail = e => {
        const mail = e.target.value
        this.setState({ mail })
    }

    handlePassword = e => {
        const password = e.target.value
        this.setState({ password })
    }

    register = () => {
        Axios.post('http://localhost:3001/register', {
            username: this.state.usernameReg, 
            mail: this.state.mailReg,
            password: this.state.passwordReg
        }).then((response) => {
            this.setState({ loginStatus: response.data.message })
        })     
    }

    login = () => {
        Axios.post('http://localhost:3001/login', {
            mail: this.state.mail, 
            password: this.state.password
        }).then((response) => {

            console.log(response)
            if (response.data.message) {
                const loginStatus = response.data.message
                this.setState({ loginStatus })
            } else {

                // console.log(response.config.xsrfCookieName)

                //mettre le token dans le local storage 
                //passer le token dans le header de la requete dans les autorisations  brear devant     (autothifier les appelss réseau avec le middleware auth)

                //post faux en base de donéée dans la base de donéé et assure toi que la route pour récupérer les posts fonctionne.




                   const user = {
                    id: response.data[0].id,
                    mail: response.data[0].mail,
                    username: response.data[0].username,
                    password: response.data[0].password,
                    age: response.data[0].age,
                    department: response.data[0].department,
                    profilimage: response.data[0].profilimage
                }

                this.props.addusers(user)

                this.setState({ goToHome: true })
            }
        })
    }

    render() {

        if (this.state.goToHome) {
            return <Redirect push to={`/home`} />
        }

        return (
            <div id="container">
            <div id="login">
                <img id="groupomania" src={logo} alt='logo'/>
                <input type="text" placeholder="Email..." onChange={this.handleMail} />
                <input type="password" placeholder="Password..." onChange={this.handlePassword} />
                <button onClick={this.login}> Se connecter </button>
            </div>
            <div id="main">
                <div className="registration">
                    <h2>Pas encore inscrit ?</h2>
                    <label>Nom d'utilisateur</label>
                    <input type="text" onChange={this.handleUsernameReg} />
                    <label>Adresse email</label>
                    <input type="text" onChange={this.handleUserMailReg} />
                    <label>Mot de passe</label>
                    <input type="password" onChange={this.handlePasswordReg} />
                    <button onClick={this.register}> S'inscrire </button>
                    <h2>{ this.state.loginStatus }</h2>
                </div>
            </div>

        </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        user: state.user
    }
}

const mapDispatchToProps = dispatch => {
    return ({
        addusers: (newObject) => {
            dispatch({ type: "USER", payload: newObject })
        }
    })
}

export default connect(mapStateToProps, mapDispatchToProps)(Connexion);
