import React, { Component } from 'react'
import './PostHome.css';
import Axios from 'axios'

class PostHome extends Component {

  state = {
    description: '',
    selectedFile: 'null',
    postArray: [],
    commentIndex: -1,
    comment: [],
    inputCommentValue: 0
}

componentDidMount () {
   this.updatePost()
}

updatePost () {

  let getTokenStringify = localStorage.getItem("token");
  let getToken = JSON.parse(getTokenStringify);

  Axios.get(`${process.env.REACT_APP_API_URL}/getPost`, { headers: {"Authorization" : `Bearer ${getToken[1]}`} }).then((response) => {

      let postArray = response.data.reverse()
      this.setState({ postArray })

  })
}

showComment = (index) => {
  console.log(index)
  if (this.state.commentIndex === index) {
    this.setState({ commentIndex: -1 })
  } else {
    this.setState({ commentIndex: index })
  }

  //add comment of post in comment

}

addPost = () => {

  let getTokenStringify = localStorage.getItem("token");
  let getToken = JSON.parse(getTokenStringify);

  var formData = new FormData();
  formData.append("image", this.state.selectedFile);
  formData.append("description", this.state.description);

  Axios.post(`${process.env.REACT_APP_API_URL}/addPost/${getToken[0]}`, formData, { headers: {"Authorization" : `Bearer ${getToken[1]}`} }).then(() => {
          this.updatePost()
    })

}


fileSelectedHandler = (e) => {
  this.setState({ selectedFile: e.target.files[0] })
}

handleDescription = (e) => {
  const description = e.target.value
  this.setState({ description })
}


addComment = (e, postId) => {
  this.setState({ inputCommentValue: e.target.value })
  if (e.key === 'Enter') {
    this.setState({ inputCommentValue: '' })

    let getTokenStringify = localStorage.getItem("token");
    let getToken = JSON.parse(getTokenStringify);

    Axios.post(`${process.env.REACT_APP_API_URL}/addComment/${getToken[0]}`, {
      postId: postId+'',
      comment: e.target.value+''
    }, { headers: {"Authorization" : `Bearer ${getToken[1]}`} }).then((response) => {
      let comment = this.state.comment
      comment.push([getToken[0], postId, e.target.value])
      this.setState({ comment })
    })
  } 
}


 render() {

    let test = []

    this.state.postArray.forEach((post, postIndex) => {

      let comment = []
      if (this.state.commentIndex === postIndex) {

        let lastComment = []
        this.state.comment.forEach(theComment => {
          if (this.state.commentIndex === theComment[1]) {
            lastComment.push(<p className='theComment'>{theComment[2]}</p>)
          }
        })

        comment.push(<div key={'comment'+postIndex}>
              {lastComment}
              <input type="text" className="addCommentInput" placeholder="ajouter un commentaire..." value={this.state.inputCommentValue} onKeyPress={(e) => this.addComment(e, postIndex)} />
        </div>)
      }

      test.push(
        <div key={'post' + postIndex}>
        <div className="postDiv">
          <h1 className="titlePost">{post.description}</h1>
          <img className="imagePost" src={post.urlimage} alt={`post ${postIndex}`} />
          <button onClick={() => this.showComment(postIndex)} className="showAddCommentBtn">...</button>
        </div>
        {comment}
        </div>)
    });

    return (
          <div id="postView">
              {test}
              <div id="lastDiv"></div>
          <div id="homeBottomBar">
            <div id="inputPost">
              <input type="text" id="description" value={this.state.description} placeholder="Description" onChange={this.handleDescription} />
              <input type="file" onChange={this.fileSelectedHandler}/>
            </div>
              <button id="addPostBtn" onClick={this.addPost}>+</button>
          </div>
        </div>
    )
  }
}

export default PostHome;
