import React, { Component, Fragment } from 'react'
import { Redirect } from 'react-router-dom'
import Axios from 'axios';
import './App.css';

class App extends Component {

  state = {
    id: this.props.match.params.id,
    user: '',
    goToProfil: false
  }

  componentDidMount () {



    Axios.get(`http://localhost:3001/user/${this.state.id}`).then((response) => {
        this.setState({ user: response.data[0].username })
    })

  }

  handleGoToProfil = () => {
    this.setState({ goToProfil: true })
  }

  render() {

    const { posts } = this.props;


    if (this.state.goToProfil) {
        return <Redirect push to={`/profil/${this.state.id}`} />
    }

    return (
      <Fragment>
        <div className="conteneur">
        <h1>Bienvenue {this.state.user}</h1>
          <p>Groupomania, un r√©seau social</p>
          <button onClick={this.handleGoToProfil}>Ajouter un profil</button>
        </div>
      </Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
      user: state.user
  }
}

export default connect(mapStateToProps)(App);
