import React, { Component, Fragment } from 'react'
import './PostHome.css';

class PostHome extends Component {

  state = {
    description: '',
    selectedFile: 'null',
}

//   componentDidMount () {

//   }


addPost = () => {
  console.log(this.state.description)
  console.log(this.state.description)
}


fileSelectedHandler = (e) => {
  this.setState({ selectedFile: e.target.files[0] })
}

handleDescription = (e) => {
  const description = e.target.value
  this.setState({ description })
}


 render() {


    return (
          <div id="postView">
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div className="postDiv"></div>
              <div id="lastDiv"></div>
          <div id="homeBottomBar">
            <div id="inputPost">
              <input type="text" id="description" value={this.state.description} placeholder="Description" onChange={this.handleDescription} />
              <input type="file" onChange={this.fileSelectedHandler}/>
            </div>
              <button id="addPostBtn" onClick={this.addPost}>+</button>
          </div>
        </div>
    )
  }
}

export default PostHome;
